      <script type="text/javascript">
         $(document).ready(function(){
            
            $('#timkiem').keyup(function(){
               $('#result').html('');
               var search = $('#timkiem').val();
               if(search!=''){
               $.ajax({
                 type: 'GET',
                 url: "{{route('searching-movie')}}",
                 data: {
                     'search': search
                 },
                 success:function(data){
                     $('#result').html(data);     
                     $('#result').css('display','inherit');
                 }
             });
            }else{
               $('#result').css('display','none');
            }
             
            })
         })
       </script>
       <script>
         $('body').click(function(){
            $('#result').css('display','none');
         });
       </script>


